require 'spec_helper'

describe Bitcoin::PSBT do

  # https://github.com/bitcoin/bips/blob/master/bip-0174.mediawiki#Test_Vectors

  it 'valid PSBT' do
    # Case: PSBT with one P2PKH input. Outputs are empty
    psbt = Bitcoin::PSBT::Tx.parse_from_payload(''.htb)
    expect(psbt.inputs.size).to eq(1)
    expect(psbt.inputs[0].non_witness_utxo).not_to be_nil
    expect(psbt.inputs[0].witness_utxo).to be_nil
    expect(psbt.tx.inputs.size).to eq(1)
    expect(psbt.inputs[0].non_witness_utxo.out[0].script_pubkey.p2pkh?).to be true
    expect(psbt.to_payload.bth).to eq('')
    expect(psbt.to_base64).to eq('')
    expect(psbt.to_payload).to eq(Bitcoin::PSBT::Tx.parse_from_base64('').to_payload)

    # Case: PSBT with one P2PKH input and one P2SH-P2WPKH input. First input is signed and finalized. Outputs are empty
    psbt = Bitcoin::PSBT::Tx.parse_from_payload(''.htb)
    expect(psbt.inputs.size).to eq(2)
    expect(psbt.inputs[0].final_script_sig).not_to be_nil
    expect(psbt.inputs[0].final_script_witness).to be_nil
    expect(psbt.to_base64).to eq('')

    # Case: PSBT with one P2PKH input which has a non-final scriptSig and has a sighash type specified. Outputs are empty
    psbt = Bitcoin::PSBT::Tx.parse_from_payload(''.htb)
    expect(psbt.inputs.size).to eq(1)
    expect(psbt.inputs[0].final_script_sig).to be_nil
    expect(psbt.inputs[0].final_script_witness).to be_nil
    expect(psbt.inputs[0].sighash_type).not_to be_nil
    expect(psbt.to_base64).to eq('')

    # Case: PSBT with one P2PKH input and one P2SH-P2WPKH input both with non-final scriptSigs. P2SH-P2WPKH input's redeemScript is available. Outputs filled.
    psbt = Bitcoin::PSBT::Tx.parse_from_payload(''.htb)
    expect(psbt.inputs.size).to eq(2)
    expect(psbt.inputs[0].final_script_sig).to be_nil
    expect(psbt.inputs[0].final_script_witness).to be_nil
    expect(psbt.inputs[1].final_script_sig).to be_nil
    expect(psbt.inputs[1].final_script_witness).to be_nil
    expect(psbt.inputs[1].redeem_script).not_to be_nil
    expect(psbt.to_base64).to eq('')

    # Case: PSBT with one P2SH-P2WSH input of a 2-of-2 multisig, redeemScript, witnessScript, and keypaths are available. Contains one signature.
    psbt = Bitcoin::PSBT::Tx.parse_from_payload(''.htb)
    expect(psbt.inputs.size).to eq(1)
    expect(psbt.inputs[0].final_script_sig).to be_nil
    expect(psbt.inputs[0].final_script_witness).to be_nil
    expect(psbt.inputs[0].redeem_script).not_to be_nil
    expect(psbt.inputs[0].witness_script).not_to be_nil
    expect(psbt.inputs[0].hd_key_paths).not_to be_nil
    expect(psbt.inputs[0].partial_sigs.size).to eq(1)
    expect(psbt.to_base64).to eq('')

    # Case: PSBT with unknown types in the inputs.
    psbt = Bitcoin::PSBT::Tx.parse_from_payload('70736274ff01003f0200000001ffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffff0000000000ffffffff010000000000000000036a010000000000000a0f0102030405060708090f0102030405060708090a0b0c0d0e0f0000'.htb)
    expect(psbt.inputs.size).to eq(1)
    expect(psbt.inputs[0].unknowns.size).to eq(1)
    expect(psbt.to_base64).to eq('cHNidP8BAD8CAAAAAf//////////////////////////////////////////AAAAAAD/////AQAAAAAAAAAAA2oBAAAAAAAACg8BAgMEBQYHCAkPAQIDBAUGBwgJCgsMDQ4PAAA=')

    # Given these two PSBTs with unknown key-value pairs
    psbt = Bitcoin::PSBT::Tx.parse_from_payload(''.htb)
    expect(psbt.unknowns.size).to eq(1)
    expect(psbt.inputs[0].unknowns.size).to eq(1)
    expect(psbt.outputs[0].unknowns.size).to eq(1)
  end

  it 'invalid PSBT' do
    # Network transaction, not PSBT format
    expect{Bitcoin::PSBT::Tx.parse_from_payload(''.htb)}.to raise_error(ArgumentError, 'Invalid PSBT magic bytes.')
    # PSBT missing outputs
    expect{Bitcoin::PSBT::Tx.parse_from_payload(''.htb)}.to raise_error(ArgumentError, 'Outputs provided does not match the number of outputs in transaction.')
    # PSBT where one input has a filled scriptSig in the unsigned tx
    expect{Bitcoin::PSBT::Tx.parse_from_payload(''.htb)}.to raise_error(ArgumentError, 'Unsigned tx does not have empty scriptSigs and scriptWitnesses.')
    # PSBT where inputs and outputs are provided but without an unsigned tx
    expect{Bitcoin::PSBT::Tx.parse_from_payload(''.htb)}.to raise_error(ArgumentError, 'No unsigned transaction was provided.')
    # PSBT with duplicate keys in an input
    expect{Bitcoin::PSBT::Tx.parse_from_payload(''.htb)}.to raise_error(ArgumentError, 'Duplicate Key, input non-witness utxo already provided.')
    # PSBT With invalid global transaction typed key
    expect{Bitcoin::PSBT::Tx.parse_from_payload(''.htb)}.to raise_error(ArgumentError, 'Invalid global transaction typed key.')
    # PSBT With invalid input witness utxo typed key
    expect{Bitcoin::PSBT::Tx.parse_from_payload(''.htb)}.to raise_error(ArgumentError, 'Invalid input witness utxo typed key.')
    # PSBT With invalid pubkey length for input partial signature typed key
    expect{Bitcoin::PSBT::Tx.parse_from_payload(''.htb)}.to raise_error(ArgumentError, 'Size of key was not the expected size for the type partial signature pubkey.')
    # PSBT With invalid redeemscript typed key
    expect{Bitcoin::PSBT::Tx.parse_from_payload(''.htb)}.to raise_error(ArgumentError, 'Invalid redeemscript typed key.')
    # PSBT With invalid witnessscript typed key
    expect{Bitcoin::PSBT::Tx.parse_from_payload(''.htb)}.to raise_error(ArgumentError, 'Invalid witnessscript typed key.')
    # PSBT With invalid bip32 typed key
    expect{Bitcoin::PSBT::Tx.parse_from_payload(''.htb)}.to raise_error(ArgumentError, 'Size of key was not the expected size for the type BIP32 keypath.')
    # PSBT With invalid non-witness utxo typed key
    expect{Bitcoin::PSBT::Tx.parse_from_payload(''.htb)}.to raise_error(ArgumentError, 'Invalid non-witness utxo typed key.')
    # PSBT With invalid final scriptsig typed key
    expect{Bitcoin::PSBT::Tx.parse_from_payload(''.htb)}.to raise_error(ArgumentError, 'Invalid final scriptsig typed key.')
    # PSBT With invalid final script witness typed key
    expect{Bitcoin::PSBT::Tx.parse_from_payload(''.htb)}.to raise_error(ArgumentError, 'Invalid final script witness typed key.')
    # PSBT With invalid pubkey in output BIP 32 derivation paths typed key
    expect{Bitcoin::PSBT::Tx.parse_from_payload(''.htb)}.to raise_error(ArgumentError, 'Size of key was not the expected size for the type BIP32 keypath.')
    # PSBT With invalid input sighash type typed key
    expect{Bitcoin::PSBT::Tx.parse_from_payload(''.htb)}.to raise_error(ArgumentError, 'Invalid input sighash type typed key.')
    # PSBT With invalid output redeemScript typed key
    expect{Bitcoin::PSBT::Tx.parse_from_payload(''.htb)}.to raise_error(ArgumentError, 'Invalid output redeemScript typed key.')
    # PSBT With invalid output witnessScript typed key
    expect{Bitcoin::PSBT::Tx.parse_from_payload(''.htb)}.to raise_error(ArgumentError, 'Invalid output witnessScript typed key.')
  end

  it 'combine PSBT' do
    # creator's PSBT
    tx = Bitcoin::Tx.new
    tx.version = 2
    tx.in << Bitcoin::TxIn.new(out_point: Bitcoin::OutPoint.from_txid('75ddabb27b8845f5247975c8a5ba7c6f336c4570708ebe230caf6db5217ae858', 0))
    tx.in << Bitcoin::TxIn.new(out_point: Bitcoin::OutPoint.from_txid('1dea7cd05979072a3578cab271c02244ea8a090bbb46aa680a65ecd027048d83', 1))
    tx.out << Bitcoin::TxOut.new(value: 149990000, script_pubkey: Bitcoin::Script.parse_from_payload('0014d85c2b71d0060b09c9886aeb815e50991dda124d'.htb))
    tx.out << Bitcoin::TxOut.new(value: 100000000, script_pubkey: Bitcoin::Script.parse_from_payload('001400aea9a2e5f0f876a588df5546e8742d1d87008f'.htb))

    psbt = Bitcoin::PSBT::Tx.new(tx)
    expect(psbt.to_payload.bth).to eq('')

    # updater update psbt
    # 75ddabb27b8845f5247975c8a5ba7c6f336c4570708ebe230caf6db5217ae858, OP_HASH160 0fb9463421696b82c833af241c78c17ddbde4934 OP_EQUAL
    prev_tx1 = Bitcoin::Tx.parse_from_payload(''.htb)
    # 1dea7cd05979072a3578cab271c02244ea8a090bbb46aa680a65ecd027048d83, OP_HASH160 b7f5faf40e3d40a5a459b1db3535f2b72fa921e8 OP_EQUAL
    prev_tx2 = Bitcoin::Tx.parse_from_payload(''.htb)

    # P2SH multisig
    redeem_script1 = Bitcoin::Script.parse_from_payload('5221029583bf39ae0a609747ad199addd634fa6108559d6c5cd39b4c2183f1ab96e07f2102dab61ff49a14db6a7d02b0cd1fbb78fc4b18312b5b4e54dae4dba2fbfef536d752ae'.htb)
    # P2WSH-P2SH
    redeem_script2 = Bitcoin::Script.parse_from_payload('00208c2353173743b595dfb4a07b72ba8e42e3797da74e87fe7d9d7497e3b2028903'.htb)

    # for script 2
    witness_script = Bitcoin::Script.parse_from_payload('522103089dc10c7ac6db54f91329af617333db388cead0c231f723379d1b99030b02dc21023add904f3d6dcf59ddb906b0dee23529b7ffb9ed50e5e86151926860221f0e7352ae'.htb)

    hk0 = Bitcoin::PSBT::HDKeyPath.new('029583bf39ae0a609747ad199addd634fa6108559d6c5cd39b4c2183f1ab96e07f', fingerprint: 'd90c6a4f', path: [2**31, 2**31, 2**31])
    hk1 = Bitcoin::PSBT::HDKeyPath.new('02dab61ff49a14db6a7d02b0cd1fbb78fc4b18312b5b4e54dae4dba2fbfef536d7', fingerprint: 'd90c6a4f', path: [2**31, 2**31, 2**31 + 1])
    hk2 = Bitcoin::PSBT::HDKeyPath.new('03089dc10c7ac6db54f91329af617333db388cead0c231f723379d1b99030b02dc', fingerprint: 'd90c6a4f', path: [2**31, 2**31, 2**31 + 2])
    hk3 = Bitcoin::PSBT::HDKeyPath.new('023add904f3d6dcf59ddb906b0dee23529b7ffb9ed50e5e86151926860221f0e73', fingerprint: 'd90c6a4f', path: [2**31, 2**31, 2**31 + 3])
    hk4 = Bitcoin::PSBT::HDKeyPath.new('03a9a4c37f5996d3aa25dbac6b570af0650394492942460b354753ed9eeca58771', fingerprint: 'd90c6a4f', path: [2**31, 2**31, 2**31 + 4])
    hk5 = Bitcoin::PSBT::HDKeyPath.new('027f6399757d2eff55a136ad02c684b1838b6556e5f1b6b34282a94b6b50051096', fingerprint: 'd90c6a4f', path: [2**31, 2**31, 2**31 + 5])

    psbt.update!(prev_tx1, redeem_script: redeem_script1, hd_key_paths: [hk0, hk1])
    psbt.update!(prev_tx2, redeem_script: redeem_script2, witness_script: witness_script, hd_key_paths: [hk3, hk2])

    psbt.outputs[0].hd_key_paths[hk4.pubkey] = hk4
    psbt.outputs[1].hd_key_paths[hk5.pubkey] = hk5

    expect(psbt.to_payload.bth).to eq('')

    # An updater which adds SIGHASH_ALL to the above PSBT
    psbt.inputs.each do |i|
      i.sighash_type = Bitcoin::SIGHASH_TYPE[:all]
    end

    expect(psbt.to_payload.bth).to eq('')

    # Given the above updated PSBT, a signer that supports SIGHASH_ALL for P2PKH and P2WPKH spends
    signer_psbt1 = Bitcoin::PSBT::Tx.parse_from_payload(''.htb)
    expect(signer_psbt1.ready_to_sign?).to be true
    # m/0'/0'/0'
    k0 = Bitcoin::Key.from_wif('cP53pDbR5WtAD8dYAW9hhTjuvvTVaEiQBdrz9XPrgLBeRFiyCbQr')
    # m/0'/0'/2'
    k2 = Bitcoin::Key.from_wif('cR6SXDoyfQrcp4piaiHE97Rsgta9mNhGTen9XeonVgwsh4iSgw6d')

    sighash0 = signer_psbt1.tx.sighash_for_input(0, signer_psbt1.signature_script(0))
    sig0 = k0.sign(sighash0, false) + [Bitcoin::SIGHASH_TYPE[:all]].pack('C')
    signer_psbt1.inputs[0].add_sig(k0.pubkey, sig0)

    sighash2 = signer_psbt1.tx.sighash_for_input(1, signer_psbt1.signature_script(1), sig_version: :witness_v0, amount: signer_psbt1.inputs[1].witness_utxo.value)
    sig2 = k2.sign(sighash2, false) + [Bitcoin::SIGHASH_TYPE[:all]].pack('C')
    # invalid sighash
    expect{signer_psbt1.inputs[1].add_sig(k2.pubkey, k2.sign(sighash2) + [Bitcoin::SIGHASH_TYPE[:single]].pack('C'))}.to raise_error(ArgumentError, 'The sighash in signature is invalid.')
    signer_psbt1.inputs[1].add_sig(k2.pubkey, sig2)

    expect(signer_psbt1.to_payload.bth).to eq('')

    # another signer create signature
    signer_psbt2 = Bitcoin::PSBT::Tx.parse_from_payload(''.htb)
    expect(signer_psbt2.ready_to_sign?).to be true

    # m/0'/0'/1'
    k1 = Bitcoin::Key.from_wif('cT7J9YpCwY3AVRFSjN6ukeEeWY6mhpbJPxRaDaP5QTdygQRxP9Au')
    # m/0'/0'/3'
    k3 = Bitcoin::Key.from_wif('cNBc3SWUip9PPm1GjRoLEJT6T41iNzCYtD7qro84FMnM5zEqeJsE')

    sighash1 = signer_psbt2.tx.sighash_for_input(0, signer_psbt2.signature_script(0))
    sig1 = k1.sign(sighash1, false) + [Bitcoin::SIGHASH_TYPE[:all]].pack('C')
    signer_psbt2.inputs[0].add_sig(k1.pubkey, sig1)

    sighash3 = signer_psbt2.tx.sighash_for_input(1, signer_psbt2.signature_script(1), sig_version: :witness_v0, amount: signer_psbt2.inputs[1].witness_utxo.value)
    sig3 = k3.sign(sighash3, false) + [Bitcoin::SIGHASH_TYPE[:all]].pack('C')
    signer_psbt2.inputs[1].add_sig(k3.pubkey, sig3)

    expect(signer_psbt2.to_payload.bth).to eq('')

    # combiner merge psbt
    combine_psbt = signer_psbt1.merge(signer_psbt2)
    expect(combine_psbt.to_payload.bth).to eq('')

    # input finalizer create psbt
    finalizer_psbt = combine_psbt.finalize!

    expect(finalizer_psbt.inputs[0].non_witness_utxo).not_to be_nil
    expect(finalizer_psbt.inputs[0].witness_utxo).to be_nil
    expect(finalizer_psbt.inputs[0].redeem_script).to be_nil
    expect(finalizer_psbt.inputs[0].witness_script).to be_nil
    expect(finalizer_psbt.inputs[0].partial_sigs.empty?).to be true
    expect(finalizer_psbt.inputs[0].hd_key_paths.empty?).to be true
    expect(finalizer_psbt.inputs[0].sighash_type).to be_nil
    expect(finalizer_psbt.inputs[0].final_script_sig).not_to be_nil
    expect(finalizer_psbt.inputs[0].final_script_witness).to be_nil

    expect(finalizer_psbt.inputs[1].non_witness_utxo).to be_nil
    expect(finalizer_psbt.inputs[1].witness_utxo).not_to be_nil
    expect(finalizer_psbt.inputs[1].redeem_script).to be_nil
    expect(finalizer_psbt.inputs[1].witness_script).to be_nil
    expect(finalizer_psbt.inputs[1].partial_sigs.empty?).to be true
    expect(finalizer_psbt.inputs[1].hd_key_paths.empty?).to be true
    expect(finalizer_psbt.inputs[1].sighash_type).to be_nil
    expect(finalizer_psbt.inputs[1].final_script_sig).not_to be_nil
    expect(finalizer_psbt.inputs[1].final_script_witness).not_to be_nil

    expect(finalizer_psbt.to_payload.bth).to eq('')

    # transaction extractor create tx
    expect(finalizer_psbt.extract_tx.to_payload.bth).to eq('')
  end

  it 'combine unknown key-value paris' do
    psbt1 = Bitcoin::PSBT::Tx.parse_from_payload(''.htb)
    psbt2 = Bitcoin::PSBT::Tx.parse_from_payload(''.htb)
    result = psbt2.merge(psbt1) # sort keys in lexicographically
    expect(result.to_payload.bth).to eq('')
  end

  it 'should judge to can sign' do
    # A Witness UTXO is provided for a non-witness input
    psbt = Bitcoin::PSBT::Tx.parse_from_payload(''.htb)
    expect(psbt.ready_to_sign?).to be false
    # redeemScript with non-witness UTXO does not match the scriptPubKey
    psbt = Bitcoin::PSBT::Tx.parse_from_payload(''.htb)
    expect(psbt.ready_to_sign?).to be false
    # redeemScript with witness UTXO does not match the scriptPubKey
    psbt = Bitcoin::PSBT::Tx.parse_from_payload(''.htb)
    expect(psbt.ready_to_sign?).to be false
    # witnessScript with witness UTXO does not match the redeemScript
    psbt = Bitcoin::PSBT::Tx.parse_from_payload(''.htb)
    expect(psbt.ready_to_sign?).to be false
  end

  # https://github.com/bitcoin/bitcoin/blob/master/test/functional/data/rpc_psbt.json
  describe "use core's test vector" do
    vectors = fixture_file('psbt/rpc_psbt.json')
    vectors['invalid'].each do |v|
      it "#{v} should raise error." do
        expect{Bitcoin::PSBT::Tx.parse_from_base64(v)}.to raise_error(ArgumentError)
      end
    end

    vectors['valid'].each do |v|
      it "#{v} should not raise error." do
        expect{Bitcoin::PSBT::Tx.parse_from_base64(v)}.not_to raise_error
      end
    end

    vectors['finalizer'].each do |v|
      it "#{v['finalize']} should finalize psbt." do
        psbt = Bitcoin::PSBT::Tx.parse_from_base64(v['finalize'])
        psbt.finalize!
        expect(psbt.to_base64).to eq(v['result'])
      end
    end

    vectors['extractor'].each do |v|
      it "#{v['extract']} should extract tx." do
        psbt = Bitcoin::PSBT::Tx.parse_from_base64(v['extract'])
        expect(psbt.extract_tx.to_payload.bth).to eq(v['result'])
      end
    end
  end

end